import React from 'react';
import { connect } from 'react-redux';
import { fetchCategories } from '../helpers/apiTrivia';

class Settings extends React.Component {
  constructor() {
    super();

    this.renderSelectorCategory = this.renderSelectorCategory.bind(this);
  }

  async renderSelectorCategory() {
    const allCategories = await fetchCategories();
    allCategories.map((el) => <option key={ el.id } value={ el.id }>{el.name}</option>);
  }

  renderSelectCategories() {
    <select>
      Categorias
      {this.renderSelectorCategory()}
    </select>;
  }

  render() {
    return (
      <div>
        <h1 data-testid="settings-title">Configurações</h1>
        <div>
          sim
        </div>
      </div>
    );
  }
}

// const mapStateToProps = (state) => ({

// });

export default connect(null, null)(Settings);
