import React from 'react';
import apiTrivia from '../helpers/apiTrivia';
import HeaderGame from '../components/HeaderGame';

class Gamepage extends React.Component {
  constructor() {
    super();

    this.state = {};
    this.answerRandom = this.answerRandom.bind(this);
    this.inicialiGame = this.inicialiGame.bind(this);
  }

  componentDidMount() {
    this.inicialiGame();
  }

  inicialiGame() {
    const token = localStorage.getItem('token');
    apiTrivia(token).then(({ results }) => this.setState({ results }));
  }

  answerRandom() {
    const { results } = this.state;
    console.log(results);
    const answers = [
      this.inicialiGame()[0].correct_answer,
      ...inicialiGame()[0].incorrect_answers,
    ];
    const answersRandom = answers
      .map((value) => ({ value, sort: Math.random() }))
      .sort((a, b) => a.sort - b.sort)
      .map(({ value }) => value);
    console.log(answersRandom);
  }

  render() {
    const { results } = this.state;
    console.log(results);
    const { category } = inicialiGame()[0];
