import React from 'react';
import apiTrivia from '../helpers/apiTrivia';
import HeaderGame from '../components/HeaderGame';
import Loading from '../components/Loading';

class Gamepage extends React.Component {
  constructor() {
    super();

    this.state = {
      results: {},
    };
    this.answerRandom = this.answerRandom.bind(this);
    this.inicialiGame = this.inicialiGame.bind(this);
  }

  componentDidMount() {
    this.inicialiGame();
  }

  inicialiGame() {
    const token = localStorage.getItem('token');
    apiTrivia(token).then((results) => this.setState({ results }));
  }

  answerRandom() {
    const answers = [];
    const { results } = this.state;
    const incorretas = results[0].incorrect_answers;
    const correta = [results[0].correct_answer];
    answers.push(incorretas
      .map((answer, index) => (
        <button
          type="button"
          data-testid={ `wrong-answer-${index}` }
          key={ index }
        >
          {answer}

        </button>)));
    answers.push(correta
      .map((answer, index) => (
        <button
          type="button"
          data-testid="correct-answer"
          key={ index }
        >
          {answer}

        </button>)));

    return answersRandom = answers
      .map((value) => ({ value, sort: Math.random() }))
      .sort((a, b) => a.sort - b.sort)
      .map(({ value }) => value);
  }

  render() {
    const { results } = this.state;
    if (Object.keys(results).length === 0) return <Loading />;
    const { category } = results[0];
    const { question } = results[0];
    return (
      <div>
        <HeaderGame />
        Sou uma PÃ¡gina de game
        <div>
          <h3 data-testid="question-category">
            { category }
          </h3>
          <p data-testid="question-text">
            { question }
          </p>
          {this.answerRandom()}
        </div>
      </div>
    );
  }
}

export default Gamepage;
