import React from 'react';
import { connect } from 'react-redux';
import { fetchCategories } from '../helpers/apiTrivia';

class Settings extends React.Component {
  constructor() {
    super();

    this.mapAllCategories = this.mapAllCategories.bind(this);
    this.renderCategories = this.renderCategories.bind(this);
  }

  async mapAllCategories() {
    const allCategories = await fetchCategories();
    const map = allCategories
      .map((el) => <option key={ el.id } value={ el.id }>{el.name}</option>);
    return map;
  }

  renderCategories() {
    return (
      <select title="categorias">
        {this.mapAllCategories()}
      </select>);
  }

  render() {
    return (
      <div>
        <h1 data-testid="settings-title">Configurações</h1>
        <div>
          <select title="categorias">
            {this.mapAllCategories()}
          </select>
          )
        </div>
      </div>
    );
  }
}

// const mapStateToProps = (state) => ({

// });

export default connect(null, null)(Settings);
