const apiTrivia = async (token) => {
  const BaseEndpoint = `https://opentdb.com/api.php?amount=5&token=${token}`;
  const response = await fetch(BaseEndpoint);
  const data = await response.json();
  return data.results;
};

export default connectapiTrivia;
