import React from 'react';
// import { connect } from 'react-redux';
import { fetchCategories } from '../helpers/apiTrivia';

class Settings extends React.Component {
  constructor() {
    super();
    this.state = {
      allcategories: [],
      value: 5,
      category: 'General Knowlodge',
    };

    this.handleChange = this.handleChange.bind(this);
    this.mapAllCategories = this.mapAllCategories.bind(this);
    this.setAllcategories = this.setAllcategories.bind(this);
  }

  componentDidMount() {
    this.setAllcategories();
  }

  async setAllcategories() {
    const response = await fetchCategories();
    this.setState({ allcategories: response });
  }

  mapAllCategories(param) {
    return param.map((el) => <option key={ el.id } value={ el.id }>{el.name}</option>);
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  render() {
    const { allcategories, value, category } = this.state;
    return (
      <div>
        <h1 data-testid="settings-title">Configurações</h1>
        <label htmlFor="value">
          Quantidade:
          <select id="value" name="quatidade" placeholder={ value } onChange={ this.handleChange }>
            <option>5</option>
            <option>10</option>
            <option>15</option>
            <option>20</option>
          </select>
        </label>
        <label htmlFor="categorias">
          Categoria:
          <select placeholder={category} id="categorias" name="categorias" onChange={ this.handleChange }>
            {this.mapAllCategories(allcategories)}
          </select>
        </label>
        <label htmlFor="value">
          Dificuldade:
          <select id="value" name="dificuldade" onChange={ this.handleChange }>
            <option value="Any Difficulty">Qualquer</option>
            <option>Fácil</option>
            <option>Normal</option>
            <option>Dificil</option>
          </select>
        </label>
        <div className="btn-div">
          <button type="button">Voltar</button>
          <button type="button">Salvar Configurações</button>
        </div>
      </div>

    );
  }
}

export default Settings;
