import { connect } from 'react-redux';
import React from 'react';

class apiTrivia extends React.Component {
  apiTrivia = async (token) => {
    const { numberOfQuestions, category, difficult } = this.props;
    let endpoint = `https://opentdb.com/api.php?amount=${numberOfQuestions}}&token=${token}`;
    if (category !== 'Any Category') endpoint += `&category=${category}`;
    if (difficult !== 'Any Difficult') endpoint += `&difficulty=${difficult}`;
    const response = await fetch(endpoint);
    const data = await response.json();
    return data.results;
  };
}


export async function fetchCategories() {
  try {
    const response = await fetch(
      'https://opentdb.com/api_category.php',
    );
    const data = await response.json();
    return data.trivia_categories;
  } catch (error) {
    console.log(error);
  }
}

const mapStateToProps = (state) => ({
  numberOfQuestions: state.numberOfQuestions,
  category: state.category,
  difficult: state.difficult,
});

export default connect(mapStateToProps, null)(apiTrivia);
