  constructor() {
    super();

    this.renderSelectorCategory = this.renderSelectorCategory.bind(this);
  }

  renderSelectorCategory() {
    fetchCategories().then((data) => {
      allCategorys = [...data];
    });
    // allCategorys.map((el) => <option key={ el.id } value={ el.id }>{el.name}</option>);
  }

  render() {
    this.renderSelectorCategory();
    return (
      <div>
        <h1 data-testid="settings-title">Configurações</h1>
