import {
  SET_SCORE,
  RESET_SCORE,
} from '../actions/game';

const INITIAL_STATE = {
  numberOfQuestions: 0,
  category: 'Any Category',
  difficult: 'Any Difficulty',
};

const settings = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case SET_SCORE:
    return {
      ...state, score: action.payload,
    };
  case RESET_SCORE:
    return INITIAL_STATE;
  default:
    return state;
  }
};

export default settings;
