const apiTrivia = async (token) => {
  const endpoint = `https://opentdb.com/api.php?amount=5&token=${token}`;
  const response = await fetch
    ((response) => response.json())
    .then((data) => data)
    .then((data) => (data.results));
};

export default apiTrivia;
